package com.daengdaeng.domain.pet.domain;import java.util.Date;import javax.persistence.CascadeType;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.OneToOne;import javax.persistence.Temporal;import javax.persistence.TemporalType;import org.springframework.data.annotation.CreatedDate;import com.daengdaeng.domain.member.domain.Member;import lombok.AccessLevel;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;@NoArgsConstructor(access = AccessLevel.PROTECTED)@Getter@Entitypublic class Pet {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private int petId;	@Column(nullable = false, length = 20)	private String name;	@Column(nullable = false)	private Date birth;	@Column(nullable = false, columnDefinition = "TINYINT(1)")	private boolean gender;	@Column(nullable = false)	private float weight;	@Column(length = 2048)	private String image;	@ManyToOne(fetch = FetchType.LAZY, cascade = CascadeType.ALL)	@JoinColumn(name = "member_id", nullable = false)	private Member member;	@OneToOne(fetch = FetchType.LAZY, cascade = CascadeType.ALL)	@JoinColumn(name = "mbti_id")	private Mbti mbti;	@Builder	public Pet(String name, Date birth, boolean gender, float weight, String image, Member member, Mbti mbti) {		this.name = name;		this.birth = birth;		this.gender = gender;		this.weight = weight;		this.image = image;		this.member = member;		this.mbti = mbti;	}}