package com.daengdaeng.domain.pet.dto.response;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import java.util.Date;import javax.validation.constraints.NotBlank;@Data@Builder@ApiModel(description = "반려동물 상세 정보")public class PetResponse {	@NotBlank	@ApiModelProperty(value = "반려동물 아이디", required = true, example = "1")	private int petId;	@NotBlank	@ApiModelProperty(value = "반려동물 이름", required = true, example = "마루")	private String name;	@NotBlank	@ApiModelProperty(value = "반려동물 생일", required = true, dataType = "Date", example = "")	private Date birth;	@NotBlank	@ApiModelProperty(value = "반려동물 성별", required = true, example = "true")	private boolean gender;	@NotBlank	@ApiModelProperty(value = "반려동물 몸무게", required = true, example = "5")	private float weight;	@ApiModelProperty(value = "반려동물 사진")	private String image;	@ApiModelProperty(value = "반려동물 mbti", required = false, example = "isfj")	private Integer mbtiId;}