package com.daengdaeng.domain.pet.repository;import com.daengdaeng.domain.pet.dto.request.PetRequest;import org.springframework.data.jpa.repository.JpaRepository;import com.daengdaeng.domain.pet.domain.Pet;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface PetRepository extends JpaRepository<Pet, Integer> {    Pet findByPetId(int petId);    @Query("select p from Pet p where p.member.memberId = :memberId")    List<Pet> findByMemberId(int memberId);    @Query("update Pet p set p.name = :#{#petRequest.name}, p.birth = :#{#petRequest.birth}, p.gender = :#{#petRequest.gender}, p.weight = :#{#petRequest.weight}, p.image = :#{#petRequest.image} where p.petId = :petId")    @Modifying    int updateByPetId(int petId, PetRequest petRequest);}