package com.daengdaeng.domain.pet.repository;import java.util.List;import java.util.Optional;import org.springframework.data.jpa.repository.JpaRepository;import com.daengdaeng.domain.pet.domain.Mbti;import com.daengdaeng.domain.pet.domain.Pet;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;@Repositorypublic interface PetRepository extends JpaRepository<Pet, Integer> {    Optional<Pet> findByPetId(int petId);    @Query("select p from Pet p where p.member.memberId = :memberId")    List<Pet> findByMemberId(int memberId);    @Modifying    @Query("update Pet p set p.mbti = :mbti where p.petId = :petId")    void updateMbtiBy(int petId, Mbti mbti);}